import React,{useContext, useEffect, useState} from 'react';
import { AuthContext } from '../context/AuthContext';
import {useHttpClient} from '../context/Http-Hook'
import LoadingSpinner from '../context/LoadingSpinner'
import {useHistory } from 'react-router-dom';

const Quiz2=(props)=>{
    const auth=useContext(AuthContext);
    const history = useHistory();
    const {isLoading,error,sendRequest,clearError}=useHttpClient();
    const i=props.i;
    const item=props.item;
    const [score,setScore]=useState(0);

    const submitHandler=async()=>{
        try{
            let data= await sendRequest(
            `http://localhost:5000/answer`,
            'POST',
            {'Content-Type':'application/json'},
            JSON.stringify({
                    subject:"English",
                    chapter:"etc2"
            }));
      
              console.log("useEffect "+data[0].questions);
              
          }
          catch(err){
              console.log(err+"dp shetty");
          }

    }

    return(
        <div>
        {isLoading && <LoadingSpinner asOverlay/>}
        {!isLoading && <form onSubmit={submitHandler}>
        <h3>Question {i} </h3>
        <h4>{item.questions[0]}</h4>
        (a) {item.questions[1]} (b) {item.questions[2]} (c){item.questions[3]} (d) {item.questions[3]} <br/><br/>

        <input type="radio" name={i} id={i+"a"} style={{"margin-right": "0"}}/> 
        <label htmlFor="a" >a</label>
        <input type="radio" name={i} id={i+"b"} style={{"margin-right":"0"}} /> 
        <label htmlFor="b">b</label>
        <input type="radio" name={i} id={i+"c"} style={{"margin-right":"0"}}/> 
        <label htmlFor="c">c</label>
        <input type="radio" name={i} id={i+"d"} style={{"margin-right":"0"}}/> 
        <label htmlFor="d">d</label>
        <hr/>

        <input type="submit" value="Submit" style={{"backgroundColor":"blue","padding":"10px","color":"white","marginLeft":"40%","width":"10%"}}/>
        </form>
        }
        </div>
        );
    }

export default Quiz2;
