import React,{useContext} from 'react'
import { AuthContext } from '../context/AuthContext';
import {useHttpClient} from '../context/Http-Hook'
import { Link, useHistory } from 'react-router-dom';

const ChaptersList=(props)=>{
    const auth=useContext(AuthContext);
    const history = useHistory();

    const clickHandler=(e)=>{
        e.preventDefault();
        if(props.subject==="E"){
            if(document.getElementById(props.chapter).checked){
                auth.CE.push(document.getElementById(props.chapter).value)
                 console.log(document.getElementById(props.chapter).value);

            }
            else{
                console.log(auth.CE);
            }
        }
    }
    return(
        <li key={props.chapter} style={{listStyle:'none'}}  onMouseEnter={clickHandler}>
        <input type="checkbox" id={props.chapter} name={props.chapter} value={props.chapter}/>
        <label htmlFor="ChapterX1"> {props.chapter} </label><br/>
        </li>
    );
}

export default ChaptersList;
