      <label htmlFor="a" >a</label>
    <input type="radio" name="answer_b" id="b" style={{"margin-right":"0"}} /> 
      <label htmlFor="b">b</label>
    <input type="radio" name="answer_c" id="c" style={{"margin-right":"0"}}/> 
      <label htmlFor="c">c</label>
    <input type="radio" name="answer_d" id="d" style={{"margin-right":"0"}}/> 
      <label htmlFor="d">d</label>
   </div>
   <input type="submit" value="Submit" style={{"backgroundColor":"blue","padding":"10px","color":"white","marginLeft":"40%","width":"10%"}}/>
 </div>
    );
}

