import React,{useContext, useEffect, useState} from 'react';
import { AuthContext } from '../context/AuthContext';
import {useHttpClient} from '../context/Http-Hook'
import LoadingSpinner from '../context/LoadingSpinner'
import {useHistory } from 'react-router-dom';

const Quiz=(props)=>{
  const auth=useContext(AuthContext);
  const [data,setData]=useState([])
  const history = useHistory();
  const {isLoading,error,sendRequest,clearError}=useHttpClient();
  useEffect(async ()=>{
    try{
        let data= await sendRequest(
        `http://localhost:5000/questions`,
        'POST',
        {'Content-Type':'application/json'},
        JSON.stringify({
                subject:"English",
                chapter:"etc2"
        }));
        setData(data);
  
          console.log("useEffect "+data[0].questions);
          
      }
      catch(err){
          console.log(err+"dp shetty");
      }
  },[])
    return(
          <div>
   <h2>Quiz Starts</h2>
   {data.length!=0 && data.map((item,i)=>{
     return  <div >
       <h3>Question {i} </h3><br/>
       {item.questions[0]}
          (a) {item.questions[1]} (b) {item.questions[2]} (c){item.questions[3]} (d) {item.questions[3]} <br/>
   
    <input type="radio" name="answer_a" id="a" style={{"margin-right": "0"}}/> 
      <label htmlFor="a" >a</label>
    <input type="radio" name="answer_b" id="b" style={{"margin-right":"0"}} /> 
      <label htmlFor="b">b</label>
    <input type="radio" name="answer_c" id="c" style={{"margin-right":"0"}}/> 
      <label htmlFor="c">c</label>
    <input type="radio" name="answer_d" id="d" style={{"margin-right":"0"}}/> 
      <label htmlFor="d">d</label>
   </div>
   })}
    
   <input type="submit" value="Submit" style={{"backgroundColor":"blue","padding":"10px","color":"white","marginLeft":"40%","width":"10%"}}/>
 </div>
    );
}

export default Quiz;
