import React,{useContext} from 'react'
import './Login.css'
import { AuthContext } from '../context/AuthContext';
import {useHttpClient} from '../context/Http-Hook'
import { Link, useHistory } from 'react-router-dom';

const Signup=()=>{ 
   const auth=useContext(AuthContext);
  const history = useHistory();
  const {isLoading,error,sendRequest,clearError}=useHttpClient();

const submitHandler=async (e)=>{
    e.preventDefault();
    const email=document.getElementById('email').value;
    const password=document.getElementById('password').value;
    const cpassword=document.getElementById('cpassword').value;
    if(password===cpassword){
      let data;
      try{
      data= await sendRequest(
          `http://localhost:5000/signup`,
          'POST',
          {'Content-Type':'application/json'},
          JSON.stringify({
                  email:email,
                  password:password,
          }));

          if(data==="successfull"){
              auth.LOGIN(document.getElementById("email").value,false)
              history.push('/');
          }
          else{
            alert(data)
          }
          
      }
      catch(err){
          console.log(err.message);
      }
    }
    // alert("email "+email+", password "+password+" cp "+cpassword);
    else
    alert('password not matching')
}

    return(
        <div>
        <h2>Signup Form</h2>

<form action="" method="post" onSubmit={submitHandler}>

  <div class="container">
    <label for="uname"><b>Username</b></label>
    <input type="text" placeholder="Enter Email" name="uname" id="email" required/>

    <label for="psw"><b>Password</b></label>
    <input type="password" placeholder="Enter Password" id="password" name="psw" required/>

    <label for="psw"><b>Confim Password</b></label>
    <input type="password" placeholder="Enter Confirm Password" id="cpassword" name="psw" required/>

        
    <button type="submit" >Signup</button>
  </div>

  <div class="container" style={{"background-color":"#f1f1f1"}}>
  </div>
</form>
</div>
    );
}

export default Signup;
