import React, { useState } from 'react'
import Chapters from './Chapter'

const Home=(props)=>{
const [Subject,setSubjects]=useState([]);
const [chapters,setChapters]=useState(false);
const [chapters_list,setChaptersList]=useState([]);

const checkHandler=(e)=>{
  e.preventDefault();
  if(!e.target.checked)
  setSubjects(Subject=>[...Subject,(e.target.value)]);
  else
  setSubjects(Subject.filter(item=>item!==e.target.value))
}

const subjectHandler=(event)=>{
  event.preventDefault();

  //fetch from backend
  setChaptersList(['chapter1','chapter2','chap3']);

  console.log(Subject,chapters_list);
  setChapters(true);
}


  return(
    
<div>
 <div>
  <h2>Select Subjects</h2>
  <form onSubmit={subjectHandler}>
    <input type="checkbox" id="Sub1" name="Sub1" value="Maths" onMouseDown={checkHandler}/>
      <label for="Sub1"> Maths </label><br/>
    <input type="checkbox" id="Sub2" name="Sub2" value="English" onMouseDown={checkHandler}/>
      <label for="Sub2"> English</label><br/>
    <input type="checkbox" id="Sub3" name="Sub3" value="GK" onMouseDown={checkHandler}/>
      <label for="Sub3"> GK </label><br/>
    <input type="checkbox" id="Sub4" name="Sub4" value="Intelligence" onMouseDown={checkHandler}/>
      <label for="Sub4"> Intelligence </label><br/>
    <input type="submit" value="Submit"/><br/>
  </form>
 </div>
 
  {
    chapters && <Chapters Subject={Subject} Chapters={chapters_list}/>
  }
  
    {/* <input type="submit" value="Start the Quizz"/><br/><br/> */}
  
 </div>
 
  
